mapper.insert:

    select SEQ_FREE_BOARD.nextval FROM dual


INSERT INTO board (
bo_no, bo_title, category_code
, bo_contents, bo_writer, bo_hit
, bo_reg_date, bo_mod_date, bo_del_yn
, atch_no, bo_pass
) VALUES (
#{boNo}, #{boTitle}, #{categoryCode}
, #{boContents}, #{boWriter}, 0
, SYSDATE, null,  'N'
,'n' ,#{boPass}
)

mapper.update:
UPDATE board
SET bo_hit = bo_hit + 1
WHERE bo_no = #{boNo}

mapper.delete:
UPDATE board
SET bo_del_yn='Y'
WHERE bo_no = #{boNo}
