mapper.update:
UPDATE member
SET
    mem_pass   =#{memPass}
  , mem_name   =#{memName}
  , mem_bir    =#{memBir}
  , mem_zip    =#{memZip}
  , mem_add1   =#{memAdd1}
  , mem_add2   =#{memAdd2}
  , mem_hp     =#{memHp}
  , mem_mail   =#{memMail}
WHERE mem_id = #{memId}

mapper.insert:
INSERT INTO member ( mem_id, mem_pass, mem_name
                   , mem_bir, mem_zip, mem_add1
                   , mem_add2, mem_hp, mem_mail
                   , mem_job, mem_hobby, mem_mileage
                   , mem_del_yn)
VALUES ( #{memId}, #{memPass}, #{memName}
       , #{memBir}, #{memZip}, #{memAdd1}
       , #{memAdd2}, #{memHp}, #{memMail}
       , #{memJob}, #{memHobby}, 0
       , 'N')
